INSERT INTO `wp_studypress_course` (`course_id`, `name`, `description`, `avancement`, `picture_id`, `post_id`) VALUES
(1, 'Medical Technology', 'BS in Medical Technology (BSMT), also known as as Bachelor of Science in Medical Laboratory Science, is a four year degree program that equips students with knowledge and skills in laboratory tests used in the detection, diagnosis, prevention, and treatment of diseases. The program consists of general education subjects (such as English and Filipino) and professional subjects (such as Clinical Chemistry and Blood Banking).', NULL, '', 217),
(2, 'Radio Technology', 'Bachelor of Science in Radiologic Technology (BSRadTech) is a four year degree program designed to train students how to operate medical imaging equipment such as X-rays, CT scanners, MRIs, sonogram machines, and 3D medical imaging programs among other computer and web-based software programs. Students are also taught how to perform associated clerical duties, including scheduling and maintaining paperwork for office and clinical machinery.', NULL, '', 218);
